# Add the project root to Python path
project_root = Path(__file__).parent
sys.path.insert(0, str(project_root))


class TestRunner:
    """Main test runner for Enhanced SciRAG."""

    def __init__(self):
        self.project_root = project_root
        self.test_results = {}
        self.start_time = time.time()

    def run_all_tests(self, coverage: bool = False) -> Dict[str, Any]:
        """Run all test suites."""
        print("ðŸš€ Enhanced SciRAG Comprehensive Testing Suite")
        print("=" * 60)

        test_suites = [
            ("Unit Tests", self.run_unit_tests),
            ("Integration Tests", self.run_integration_tests),
            ("Performance Tests", self.run_performance_tests),
            ("Real Document Tests", self.run_real_document_tests),
            ("Backward Compatibility Tests", self.run_backward_compatibility_tests),
            ("Error Handling Tests", self.run_error_handling_tests),
            ("Configuration Tests", self.run_configuration_tests),
        ]

        results = {}
        total_passed = 0
        total_failed = 0

        for suite_name, test_func in test_suites:
            print(f"\nðŸ“‹ Running {suite_name}...")
            print("-" * 40)

            try:
                suite_result = test_func()
                results[suite_name] = suite_result

                if suite_result['passed']:
                    print(
                        f"âœ… {suite_name}: PASSED ({suite_result['tests_passed']}/{suite_result['total_tests']})")
                    total_passed += suite_result['tests_passed']
                else:
                    print(
                        f"âŒ {suite_name}: FAILED ({suite_result['tests_passed']}/{suite_result['total_tests']})")
                    total_failed += suite_result['tests_failed']

            except Exception as e:
                print(f"ðŸ’¥ {suite_name}: ERROR - {e}")
                results[suite_name] = {
                    'passed': False,
                    'error': str(e),
                    'tests_passed': 0,
                    'tests_failed': 1,
                    'total_tests': 1
                }
                total_failed += 1

        # Generate summary
        total_tests = total_passed + total_failed
        duration = time.time() - self.start_time

        print(f"\nðŸ“Š Test Summary")
        print("=" * 30)
        print(f"Total Tests: {total_tests}")
        print(f"Passed: {total_passed}")
        print(f"Failed: {total_failed}")
        print(f"Success Rate: {(total_passed/total_tests*100):.1f}%")
        print(f"Duration: {duration:.2f}s")

        if coverage:
            self.run_coverage_report()

        return {
            'total_tests': total_tests,
            'passed': total_passed,
            'failed': total_failed,
            'success_rate': total_passed / total_tests * 100,
            'duration': duration,
            'suites': results
        }

