    def run_performance_tests(self) -> Dict[str, Any]:
        """Run performance tests."""
        print("âš¡ Testing performance...")

        try:
            import time
            from scirag.enhanced_processing import MathematicalProcessor

            # Test processing speed
            processor = MathematicalProcessor(enable_sympy=True)
            start_time = time.time()

            # Process multiple equations
            equations = [
                "E = mc^2",
                "\\frac{d}{dx}[f(x)] = \\lim_{h \\to 0} \\frac{f(x+h) - f(x)}{h}",
                "\\int_{-\\infty}^{\\infty} e^{-x^2} dx = \\sqrt{\\pi}",
                "\\sum_{i=1}^{n} i = \\frac{n(n+1)}{2}",
                "\\nabla \\times \\vec{E} = -\\frac{\\partial \\vec{B}}{\\partial t}"]

            for equation in equations:
                result = processor.process_equation(equation)
                assert result is not None

            processing_time = time.time() - start_time
            print(
                f"  âœ… Processing speed: {processing_time:.3f}s for {len(equations)} equations")

            # Test memory usage
            import psutil
            process = psutil.Process()
            memory_usage = process.memory_info().rss / 1024 / 1024  # MB
            print(f"  âœ… Memory usage: {memory_usage:.1f} MB")

        except Exception as e:
            print(f"  âŒ Performance test: {e}")
            return {
                'passed': False,
                'tests_passed': 0,
                'tests_failed': 1,
                'total_tests': 1}

        return {
            'passed': True,
            'tests_passed': 2,
            'tests_failed': 0,
            'total_tests': 2}

    def run_real_document_tests(self) -> Dict[str, Any]:
        """Run tests with real scientific documents."""
        print("ðŸ“„ Testing with real documents...")

        try:
            # Check if test documents exist
            txt_files_dir = self.project_root / "txt_files"
            if not txt_files_dir.exists():
                print("  âš ï¸  No test documents found, skipping real document tests")
                return {
                    'passed': True,
                    'tests_passed': 0,
                    'tests_failed': 0,
                    'total_tests': 0}

            txt_files = list(txt_files_dir.glob("*.txt"))
            if not txt_files:
                print("  âš ï¸  No .txt files found, skipping real document tests")
                return {
                    'passed': True,
                    'tests_passed': 0,
                    'tests_failed': 0,
                    'total_tests': 0}

            # Test with first document
            from scirag.enhanced_processing import EnhancedChunker
            chunker = EnhancedChunker(chunk_size=1000, overlap_ratio=0.2)

            with open(txt_files[0], 'r', encoding='utf-8') as f:
                content = f.read()

            chunks = chunker.chunk_text(content, txt_files[0].stem)
            print(f"  âœ… Processed {txt_files[0].name}: {len(chunks)} chunks")

        except Exception as e:
            print(f"  âŒ Real document test: {e}")
            return {
                'passed': False,
                'tests_passed': 0,
                'tests_failed': 1,
                'total_tests': 1}

        return {
            'passed': True,
            'tests_passed': 1,
            'tests_failed': 0,
            'total_tests': 1}

