    def test_performance(self) -> bool:
        """Test performance under load."""
        print("ðŸ§ª Testing performance under load...")
        
        try:
            # Test multiple concurrent requests
            import concurrent.futures
            
            def make_request():
                query_data = {
                    "query": f"Test query {time.time()}",
                    "enable_enhanced_processing": True
                }
                response = self.session.post(
                    f"{self.base_url}/query",
                    json=query_data,
                    timeout=30
                )
                return response.status_code == 200
            
            # Run 10 concurrent requests
            with concurrent.futures.ThreadPoolExecutor(max_workers=5) as executor:
                futures = [executor.submit(make_request) for _ in range(10)]
                results = [future.result() for future in concurrent.futures.as_completed(futures)]
            
            success_rate = sum(results) / len(results)
            
            if success_rate >= 0.8:  # 80% success rate
                print(f"âœ… Performance test passed - Success rate: {success_rate:.1%}")
                self.test_results.append(("performance_test", True, {"success_rate": success_rate}))
                return True
            else:
                print(f"âŒ Performance test failed - Success rate: {success_rate:.1%}")
                self.test_results.append(("performance_test", False, {"success_rate": success_rate}))
                return False
                
        except Exception as e:
            print(f"âŒ Performance test failed: {e}")
            self.test_results.append(("performance_test", False, str(e)))
            return False
    
